          innerRadius={100}
          colorScale={["#32aeca", "#8e3ace"]}
          data={[
            { x: "IOS", y: 80 },
            { x: "Android", y: 20 }
          ]}
        />
      </div>
      <div className="pie-legend">
        <VictoryLegend
          width={100}
          data={[
            { name: "IOS", symbol: { fill: "#32aeca", type: "square" } },
            { name: "Android", symbol: { fill: "#8e3ace" } }
          ]}
