import React from "react";
import {ResponsiveBar} from "@nivo/bar";
import { connect } from "react-redux";
import _ from "lodash";

class BarChartSum extends React.Component {
  render() {
    const dataHeat = this.props.getDataHeatChart;
    const list = dataHeat.getDataHeatChart.map(e => e.data.map(a => a.y));
    console.log(list)
    return (
      <div style={{ width: "200px" }}>
        <ResponsiveBar
          data={[
            {country: "Sun", "sum": _.sum(list[0])},
            {country: "Mon", "sum": _.sum(list[1])},
            {country: "Tue", "sum": _.sum(list[2])},
            {country: "Wed", "sum": _.sum(list[3])},
            {country: "Thu", "sum": _.sum(list[4])},
            {country: "Fri", "sum": _.sum(list[5])},
            {country: "Sat", "sum": _.sum(list[6])}
          ]}
          keys={["sum"]}
          indexBy="country"
          layout="horizontal"

        />
      </div>
    );
  }
}
function mapStateToProps(state) {
  return {
    getDataHeatChart: state.getDataHeatChart
  };
}

export default connect(mapStateToProps, null)(BarChartSum);
