    console.log(getDataHeat.getDataHeatChart);
    const options = {
      chart : {
        height: 200,
        type:"heatmap"
      },
      title: {
        text: 'Device by hour',
        align: 'left',
        margin: 10,
        offsetX: 0,
        offsetY: 0,
        floating: false,
        style: {
          fontSize:  '16px',
          color:  '#263238'
        },
    },

      // colors: [function({ value, seriesIndex, w }) {
