import { ActionTypes } from "../constants/constants";

let initialState = {};
export default function(state = initialState, action) {
    switch (action.type) {
        case ActionTypes.SHOW_LOADING_PIECHART:
            return { isLoading: action.isLoading };
        case ActionTypes.HIDE_LOADING_PIECHART:
            return { isLoading: action.isLoading };
        case ActionTypes.SHOW_LOADING_BARCHART:
            return { isLoadingBar: action.isLoadingBar };
        case ActionTypes.HIDE_LOADING_BARCHRT:
            console.log(action.isLoadingBar)
            return { isLoadingBar: action.isLoadingBar };
        default:
            return state;
    }
}
