import React from 'react'

export const PieChartLegend = (data) => {
  const { style, x, y } = data

  const sum = data.data.reduce((a,b) => a.x + b.y, 0)
  console.log(x,y,sum)
  return (
    <svg>
      <g>
        <circle cx={x - 12} cy={y - 6} r="7" fill={style.fill} />
      </g>
      <text x={x} y={y}>
        {data.datum.x}
      </text>
      <text x={x + 200} y={y}>
        {data.datum.y}
      </text>
      <text x={x} y={y + 30} fontSize={25}>
        {data.datum.y / sum * 100}%
      </text>
    </svg >
  )
}
