import React from "react";
import { VictoryPie, VictoryLegend } from "victory";
import "./StylePie.css";
import { connect } from "react-redux";
import { setData } from "../../actions/index";

class PieChart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      name:"",
      percent:"",
    };
  }
  async componentDidMount() {
    const sum = setData();
    this.setState({
      data: (await sum.payload).data,
      name: (await sum.payload).data.name,
      percent: (await sum.payload).data.percent
    });
    console.log(this.state.data);
  }
  render() {
    return (
      <div>
        <div>DeviceTypes</div>
        <svg viewBox="20 20 1500 1500" width="1500" height="1500">
          <VictoryPie
            standalone={false}
            style={{ labels: { fill: "none"} }}
            innerRadius={100}
            colorScale={["#32aeca", "#8e3ace"]}
            data={this.state.data}
            x="name"
            y="percent"
          />

          <VictoryLegend
            standalone={false}
            data={this.state.data}
            x={400}
            y={200}
          />
          {console.log(this.state.data.map(e=>e.name))}
    <text x="433" y="237" >{this.state.percent[0]} %</text>
          <text x="725" y="220" >{this.state.percent[0] * 191}</text>
          <text x="433" y="267" >20%</text>
          <text x="725" y="250" >20</text>
        </svg>
      </div>
    );
  }
}
function mapStateToProps({ data }) {
  return { data };
}

export default connect(mapStateToProps, null)(PieChart);
