import React from "react";
import ReactApexChart from "react-apexcharts";
import { connect } from "react-redux";

class HeatChart extends React.Component {
  render() {
    const getDataHeat = this.props.getDataHeatChart;
    console.log(this.props.getDataHeatChart.getDataHeatChart.map(e => e.name));
    console.log(getDataHeat);
    const options = {
      chart: {
        height: 350,
        type: "heatmap"
      },
      dataLabel: {
        enable: false
      },
      colors: ["#008FFB"],
      title: {
        text: "HeatMap Chart (Single color)"
      }
    };
    return (
      <div id="chart">
        <ReactApexChart
          type="heatmap"
          height={350}
          series={getDataHeat}
          options ={ options}
        />
      </div>
    );
  }
}
function mapStateToProps(state) {
  return {
    getDataHeatChart: state.getDataHeatChart
  };
}
export default connect(mapStateToProps, null)(HeatChart);


