import React from "react";
import { connect } from "react-redux";
import { VictoryChart, VictoryBar, VictoryAxis,VictoryLabel, VictoryLegend } from "victory";
import BarChartLabel from './BarChartLabel';
import _ from 'lodash';

class BarChart extends React.Component {
  render() {
    const { getDataBarChart } = this.props;
    _.reverse(getDataBarChart);
    return (
      <div style={{ width: "500px", height: "300px" }}>
        <VictoryChart domainPadding={{ x: 15 }}>
          <VictoryBar
            standalone={false}
            barRatio={1.2}
            style={{ data: { fill: "#ccc" }, labels: { fill: "white" } }}
            horizontal
            data={getDataBarChart}
            x="day"
            y="number"
          />
          <VictoryAxis crossAxis/>

          <VictoryLabel x={20} y={30}
            standalone={false}
            text="Day"
          />
          <VictoryLabel x={350} y={30}
            standalone={false}
            text="Number"
          />

          <VictoryLegend
            standalone={false}
            data={getDataBarChart}
            rowGutter={{ top: 0, bottom: 15 }}
            dataComponent={<BarChartLabel/>}
            x={100}
            y={50}
          />
        </VictoryChart>
      </div>
    );
  }
}
function mapStateToProps(state) {
  return { getDataBarChart: state.getDataBarChart };
}

export default connect(mapStateToProps, null)(BarChart);
