import React from 'react';
import { connect } from 'react-redux';
import HighCharts from 'highcharts';
import HighChartsReact from 'highcharts-react-official';


class HeatChart extends React.Component{
  render() {
    const options = {
      title:{text :'Device by hour'},
      series: [{
        data:[1,2,3]
      }]
    }
    const {getDataHeatChart} = this.props.getDataHeatChart;
    const name = getDataHeatChart.map(e => e.name);
    const data = getDataHeatChart.map(e => e.data);
    console.log(name, data, data[0]);
    return (
      HighCharts.chart({
        chart:{
          type: 'heatmap'
        },
        title: {
          text: 'Device by hour'
        },
        xAxis:{
          categories:['1','2','3']
        }
      })
    );
  }
}
function mapStateToProps(state){
  return{
    getDataHeatChart : state.getDataHeatChart
  };
}
export default connect(mapStateToProps, null)(HeatChart);
