import React, { Component } from 'react';
import Chart from 'react-apexcharts'

class HeatChart extends Component {

  constructor(props) {
    super(props);

    this.state = {
        options : {
          chart:{
            id: "basic-bar"
          },
          xaxis:{
            categories : ["W1", "W2", "W3", "W4"]}
        },
        series: [
          {
            name: "Series 1",
            data: [12, 23, 34, 45]
          },
          {
            name: "Series 2",
            data: [23,32,34,67]
          }
        ]

    }
  }

  render() {

    return (
      <div>
        <Chart series={this.state.series} type="heatmap" width="380" />
      </div>
    );
  }
}

export default HeatChart;
