import React from "react";

export const BarChartLabel = data => {
  const index = data.index;
  const xValue = data.data[index].day;
  const yValue = data.data[index].number;
  const x = data.x;
  const y = data.y;
  return (
    <svg>
      {
       <text x={x + 12} y={y - 10} style={{}}>
          {xValue}
        </text>
      }
      {
        <text x={x + 360} y={y - 10} style={{ direction: "rtl" }}>
          {yValue}
        </text>
      }
    </svg>
  );

};
