import React from "react";
import Chart from "react-apexcharts";
import { connect } from "react-redux";

class HeatChart extends React.Component {
  render() {
    const getDataHeat = this.props.getDataHeatChart;
    const data = getDataHeat.getDataHeatChart.map(e => e.data);
    console.log(getDataHeat.getDataHeatChart);
    const options = {
      chart : {
        height: 200,
        type:"heatmap"
      },
      xaxis : {
        categories : ["00:00","01:00", "02:00", "03:00", "04:00"]
      }
    }
    return (
      <div id="chart">
        <Chart
        options={options}
        series={getDataHeat.getDataHeatChart}
        type="heatmap"
        height="200"
         />
      </div>
    );
  }
}
function mapStateToProps(state) {
  return {
    getDataHeatChart: state.getDataHeatChart
  };
}
export default connect(mapStateToProps, null)(HeatChart);

