import { ActionTypes } from "../constants/constants";
// import { setData } from "../actions/index";

let initialState = [];
export default function(state = initialState, action) {
  switch (action.type) {
    case ActionTypes.SHOW_DATA:
      return [action.payload.data[0], ...state];
    default:
      return state;
  }
}
