import React, { Component } from 'react';
import Chart from 'react-apexcharts'

class HeatChart extends Component {

  constructor(props) {
    super(props);

    this.state = {
      options : {
        series: [
          {
            name: "Series 1",
            data: [{
              x: 'W1',
              y: 22
            }, {
              x: 'W2',
              y: 29
            }, {
              x: 'W3',
              y: 13
            }, {
              x: 'W4',
              y: 32
            }]
          },
          {
            name: "Series 2",
            data: [{
              x: 'W1',
              y: 43
            }, {
              x: 'W2',
              y: 43
            }, {
              x: 'W3',
              y: 43
            }, {
              x: 'W4',
              y: 43
            }]
          }
        ]
      }
    }
  }

  render() {

    return (
      <div className="donut">
        <Chart series={this.state.options} type="heatmap" width="380" />
      </div>
    );
  }
}

export default HeatChart;
