import React from "react";
import { VictoryPie, VictoryLegend } from "victory";
import "./StylePie.css";
import { connect } from "react-redux";
import { setData } from "../../actions/index";

class PieChart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: []
    };
  }
  async componentDidMount() {
    const sum = setData();
    this.setState({
      data: (await sum.payload).data
    });
    console.log(this.state.data);
  }
  render() {
    return (
      <div className="pie-pie">

        <div className="header">DeviceTypes</div>
        <div className="pie-chart">
          <VictoryPie
            style={{ labels: { fill: "none" } }}
            innerRadius={100}
            colorScale={["#32aeca", "#8e3ace"]}
            data={this.state.data}
            x="name"
            y="percent"
          />
        </div>

        <svg viewBox="0 0 200 300" >
          <VictoryLegend
            standalone={false}
            data={[
              { name: "IOS", symbol: { fill: "#32aeca", type: "square" } },
              { name: "Android", symbol: { fill: "#8e3ace" } }
            ]}
          />
        </svg>
      </div>
    );
  }
}
function mapStateToProps({ data }) {
  return { data };
}

export default connect(mapStateToProps, null)(PieChart);
