import React from "react";
import { VictoryPie, VictoryLegend } from "victory";
import "./StylePie.css";
import { connect } from "react-redux";
import { setData } from "../../actions/index";

class PieChart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: []
    };
  }
  async componentDidMount() {
    const sum = setData();
    this.setState({
      data: (await sum.payload).data
    });
    console.log(this.state.data);
  }
  render() {
    return (
      <div>
        <div>DeviceTypes</div>
        <svg viewBox="20 20 1500 1500" width="1500" height="1500">
          <VictoryPie
            standalone={false}
            style={{ labels: { fill: "none"} }}
            innerRadius={100}
            colorScale={["#32aeca", "#8e3ace"]}
            data={this.state.data}
            x="name"
            y="percent"
          />

          <VictoryLegend
            standalone={false}
            data={this.state.data}
            x={400}
            y={200}
          />
          <text x="200" y="35" class="small">My</text>
          <text x="400" y="35" class="heavy">cat</text>
          <text x="550" y="55" class="small">is</text>
          <text x="650" y="55" class="Rrrrr">Grumpy!</text>
        </svg>
      </div>
    );
  }
}
function mapStateToProps({ data }) {
  return { data };
}

export default connect(mapStateToProps, null)(PieChart);
