import {ActionTypes} from '../constants/constants';

let initialState = [];
export default function(state = initialState,action){
  switch (action.type){
    case ActionTypes.SHOW_LOADING:
      return {...action.isLoading};
    case ActionTypes.HIDE_LOADING:
      return {...action.isLoading};
    default:
      return state;
  }
}
